INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 0:00:00') as dateinserted, raw_0 as raw, unique_0 as uniq FROM qu_pap_dailyimpressions WHERE raw_0 > 0 OR unique_0 > 0;