INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-13 12:00:00') as dateinserted, raw_13 as raw, unique_13 as uniq, declined_13 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 13 AND (raw_13 > 0 OR unique_13 > 0 OR declined_13 > 0);