INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-20 12:00:00') as dateinserted, raw_20 as raw, unique_20 as uniq, declined_20 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 20 AND (raw_20 > 0 OR unique_20 > 0 OR declined_20 > 0);