INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-21 12:00:00') as dateinserted, raw_21 as raw, unique_21 as uniq, declined_21 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 21 AND (raw_21 > 0 OR unique_21 > 0 OR declined_21 > 0);