INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 1:00:00') as dateinserted, raw_1 as raw, unique_1 as uniq FROM qu_pap_dailyimpressions WHERE raw_1 > 0 OR unique_1 > 0;