INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-23 12:00:00') as dateinserted, raw_23 as raw, unique_23 as uniq, declined_23 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 23 AND (raw_23 > 0 OR unique_23 > 0 OR declined_23 > 0);