INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-26 12:00:00') as dateinserted, raw_26 as raw, unique_26 as uniq, declined_26 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 26 AND (raw_26 > 0 OR unique_26 > 0 OR declined_26 > 0);