INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-27 12:00:00') as dateinserted, raw_27 as raw, unique_27 as uniq, declined_27 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 27 AND (raw_27 > 0 OR unique_27 > 0 OR declined_27 > 0);