INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-28 12:00:00') as dateinserted, raw_28 as raw, unique_28 as uniq, declined_28 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 28 AND (raw_28 > 0 OR unique_28 > 0 OR declined_28 > 0);