INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-31 12:00:00') as dateinserted, raw_31 as raw, unique_31 as uniq, declined_31 as `declined` FROM qu_pap_monthlyclicks WHERE DAY(LAST_DAY(month)) >= 31 AND (raw_31 > 0 OR unique_31 > 0 OR declined_31 > 0);