INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 2:00:00') as dateinserted, raw_2 as raw, unique_2 as uniq FROM qu_pap_dailyimpressions WHERE raw_2 > 0 OR unique_2 > 0;