INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 4:00:00') as dateinserted, raw_4 as raw, unique_4 as uniq FROM qu_pap_dailyimpressions WHERE raw_4 > 0 OR unique_4 > 0;