INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 8:00:00') as dateinserted, raw_8 as raw, unique_8 as uniq FROM qu_pap_dailyimpressions WHERE raw_8 > 0 OR unique_8 > 0;