INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 9:00:00') as dateinserted, raw_9 as raw, unique_9 as uniq FROM qu_pap_dailyimpressions WHERE raw_9 > 0 OR unique_9 > 0;