INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 10:00:00') as dateinserted, raw_10 as raw, unique_10 as uniq FROM qu_pap_dailyimpressions WHERE raw_10 > 0 OR unique_10 > 0;