INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 13:00:00') as dateinserted, raw_13 as raw, unique_13 as uniq FROM qu_pap_dailyimpressions WHERE raw_13 > 0 OR unique_13 > 0;