INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 14:00:00') as dateinserted, raw_14 as raw, unique_14 as uniq FROM qu_pap_dailyimpressions WHERE raw_14 > 0 OR unique_14 > 0;