INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 15:00:00') as dateinserted, raw_15 as raw, unique_15 as uniq FROM qu_pap_dailyimpressions WHERE raw_15 > 0 OR unique_15 > 0;