INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 16:00:00') as dateinserted, raw_16 as raw, unique_16 as uniq FROM qu_pap_dailyimpressions WHERE raw_16 > 0 OR unique_16 > 0;