INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 19:00:00') as dateinserted, raw_19 as raw, unique_19 as uniq FROM qu_pap_dailyimpressions WHERE raw_19 > 0 OR unique_19 > 0;