INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 21:00:00') as dateinserted, raw_21 as raw, unique_21 as uniq FROM qu_pap_dailyimpressions WHERE raw_21 > 0 OR unique_21 > 0;