INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 23:00:00') as dateinserted, raw_23 as raw, unique_23 as uniq FROM qu_pap_dailyimpressions WHERE raw_23 > 0 OR unique_23 > 0;