INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 0:00:00') as dateinserted, raw_0 as raw, unique_0 as uniq, declined_0 as `declined` FROM qu_pap_dailyclicks WHERE raw_0 > 0 OR unique_0 > 0 OR declined_0 > 0 ;