INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 1:00:00') as dateinserted, raw_1 as raw, unique_1 as uniq, declined_1 as `declined` FROM qu_pap_dailyclicks WHERE raw_1 > 0 OR unique_1 > 0 OR declined_1 > 0 ;