INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 2:00:00') as dateinserted, raw_2 as raw, unique_2 as uniq, declined_2 as `declined` FROM qu_pap_dailyclicks WHERE raw_2 > 0 OR unique_2 > 0 OR declined_2 > 0 ;