INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 3:00:00') as dateinserted, raw_3 as raw, unique_3 as uniq, declined_3 as `declined` FROM qu_pap_dailyclicks WHERE raw_3 > 0 OR unique_3 > 0 OR declined_3 > 0 ;