INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 4:00:00') as dateinserted, raw_4 as raw, unique_4 as uniq, declined_4 as `declined` FROM qu_pap_dailyclicks WHERE raw_4 > 0 OR unique_4 > 0 OR declined_4 > 0 ;