INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 8:00:00') as dateinserted, raw_8 as raw, unique_8 as uniq, declined_8 as `declined` FROM qu_pap_dailyclicks WHERE raw_8 > 0 OR unique_8 > 0 OR declined_8 > 0 ;