INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 9:00:00') as dateinserted, raw_9 as raw, unique_9 as uniq, declined_9 as `declined` FROM qu_pap_dailyclicks WHERE raw_9 > 0 OR unique_9 > 0 OR declined_9 > 0 ;