INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 10:00:00') as dateinserted, raw_10 as raw, unique_10 as uniq, declined_10 as `declined` FROM qu_pap_dailyclicks WHERE raw_10 > 0 OR unique_10 > 0 OR declined_10 > 0 ;