INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 13:00:00') as dateinserted, raw_13 as raw, unique_13 as uniq, declined_13 as `declined` FROM qu_pap_dailyclicks WHERE raw_13 > 0 OR unique_13 > 0 OR declined_13 > 0 ;