INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 14:00:00') as dateinserted, raw_14 as raw, unique_14 as uniq, declined_14 as `declined` FROM qu_pap_dailyclicks WHERE raw_14 > 0 OR unique_14 > 0 OR declined_14 > 0 ;