INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 17:00:00') as dateinserted, raw_17 as raw, unique_17 as uniq, declined_17 as `declined` FROM qu_pap_dailyclicks WHERE raw_17 > 0 OR unique_17 > 0 OR declined_17 > 0 ;