INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 19:00:00') as dateinserted, raw_19 as raw, unique_19 as uniq, declined_19 as `declined` FROM qu_pap_dailyclicks WHERE raw_19 > 0 OR unique_19 > 0 OR declined_19 > 0 ;