INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 20:00:00') as dateinserted, raw_20 as raw, unique_20 as uniq, declined_20 as `declined` FROM qu_pap_dailyclicks WHERE raw_20 > 0 OR unique_20 > 0 OR declined_20 > 0 ;