INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 22:00:00') as dateinserted, raw_22 as raw, unique_22 as uniq, declined_22 as `declined` FROM qu_pap_dailyclicks WHERE raw_22 > 0 OR unique_22 > 0 OR declined_22 > 0 ;