INSERT INTO qu_pap_clicks (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq,declined) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, CONCAT(DATE(day), ' 23:00:00') as dateinserted, raw_23 as raw, unique_23 as uniq, declined_23 as `declined` FROM qu_pap_dailyclicks WHERE raw_23 > 0 OR unique_23 > 0 OR declined_23 > 0 ;