INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-1 12:00:00') as dateinserted, raw_1 as raw, unique_1 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 1 AND (raw_1 > 0 OR unique_1 > 0);