INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-5 12:00:00') as dateinserted, raw_5 as raw, unique_5 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 5 AND (raw_5 > 0 OR unique_5 > 0);