INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-6 12:00:00') as dateinserted, raw_6 as raw, unique_6 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 6 AND (raw_6 > 0 OR unique_6 > 0);