INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-7 12:00:00') as dateinserted, raw_7 as raw, unique_7 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 7 AND (raw_7 > 0 OR unique_7 > 0);