INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-9 12:00:00') as dateinserted, raw_9 as raw, unique_9 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 9 AND (raw_9 > 0 OR unique_9 > 0);