INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-12 12:00:00') as dateinserted, raw_12 as raw, unique_12 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 12 AND (raw_12 > 0 OR unique_12 > 0);