INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-17 12:00:00') as dateinserted, raw_17 as raw, unique_17 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 17 AND (raw_17 > 0 OR unique_17 > 0);