INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-19 12:00:00') as dateinserted, raw_19 as raw, unique_19 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 19 AND (raw_19 > 0 OR unique_19 > 0);