INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-20 12:00:00') as dateinserted, raw_20 as raw, unique_20 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 20 AND (raw_20 > 0 OR unique_20 > 0);