INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-24 12:00:00') as dateinserted, raw_24 as raw, unique_24 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 24 AND (raw_24 > 0 OR unique_24 > 0);