INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-27 12:00:00') as dateinserted, raw_27 as raw, unique_27 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 27 AND (raw_27 > 0 OR unique_27 > 0);