INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-29 12:00:00') as dateinserted, raw_29 as raw, unique_29 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 29 AND (raw_29 > 0 OR unique_29 > 0);