INSERT INTO qu_pap_impressions (userid,campaignid,bannerid,parentbannerid,countrycode,cdata1,cdata2,channel,dateinserted,raw,uniq) SELECT userid, campaignid, bannerid, parentbannerid, countrycode, cdata1, cdata2, channel, DATE_FORMAT(month, '%Y-%m-30 12:00:00') as dateinserted, raw_30 as raw, unique_30 as uniq FROM qu_pap_monthlyimpressions WHERE DAY(LAST_DAY(month)) >= 30 AND (raw_30 > 0 OR unique_30 > 0);